module.exports = async function() {
  const contentUrl = data['content-url-input'] || '';
  const languagesStr = data['target-languages-input'] || '';

  if (!contentUrl || !languagesStr) throw new Error('URL and languages required');

  // Extract content ID
  const urlParts = contentUrl.split('/');
  const lastPart = urlParts[urlParts.length - 1];
  const contentId = lastPart.split('-').pop();
  if (!contentId || contentId.length < 10) throw new Error('Invalid content ID');

  // Parse languages
  const languages = languagesStr.split(',').map(l => l.trim().toLowerCase()).filter(l => l);
  if (languages.length === 0) throw new Error('No valid languages');

  console.log(`Content: ${contentId}, Languages: ${languages.join(', ')}`);

  // Download original
  let fullUrl = contentUrl.startsWith('http') ? contentUrl : `https://${contentUrl}`;
  const response = await api.fetch(fullUrl);
  const originalHtml = await response.text();
  if (!originalHtml) throw new Error('Empty content');

  console.log(`Downloaded ${originalHtml.length} chars`);

  // Return data structure with languages list for loop
  return JSON.stringify({
    contentId,
    languages,
    originalHtml
  });
};

module.exports = async function() {
  const setupData = JSON.parse(data['setup'] || '{}');
  const languages = setupData.languages || [];

  if (languages.length === 0) {
    throw new Error('No languages to translate');
  }

  console.log(`Extracted ${languages.length} languages: ${languages.join(', ')}`);
  return languages.join('\n');
};

subNodes.script:
module.exports = async function() {
  const setupData = JSON.parse(data['setup'] || '{}');
  const lang = data['translate-loop-loop'] || '';
  const contentId = setupData.contentId;
  const originalHtml = setupData.originalHtml;

  if (!contentId) throw new Error('Missing content ID');

  console.log(`[${lang}] Start`);

  // Check existing
  const versionsResp = await api.frevanaApi.request('GET', `/s3/content/${contentId}/language-versions`);

  if (versionsResp.ok && versionsResp.data?.versions) {
    const existing = versionsResp.data.versions.find(v => v.language_code === lang);
    if (existing) {
      let url = existing.url || '';
      if (url && !url.startsWith('http')) url = `https://${url}`;
      console.log(`[${lang}] Exists`);
      return JSON.stringify({ skip: true, lang, status: 'existing', id: existing.content_id, url });
    }
  }

  // Prepare new
  const prepResp = await api.frevanaApi.request('POST', `/s3/content/${contentId}/prepare-translate`, { target_language: lang });
  if (!prepResp.ok) throw new Error(`[${lang}] Prepare failed`);

  console.log(`[${lang}] Ready`);
  return JSON.stringify({
    skip: false,
    lang,
    html: originalHtml,
    newId: prepResp.data.new_content_id,
    uploadUrl: prepResp.data.upload_url
  });
};

module.exports = async function() {
  const prep = JSON.parse(data['check-prepare'] || '{}');
  const ai = data['translate'] || '';
  const lang = prep.lang;

  if (prep.skip) {
    console.log(`[${lang}] Skip`);
    return JSON.stringify({ lang, status: 'existing', id: prep.id, url: prep.url });
  }

  if (!ai) throw new Error(`[${lang}] No content`);

  // Upload
  const up = await api.fetch(prep.uploadUrl, {
    method: 'PUT',
    headers: { 'Content-Type': 'text/html' },
    body: ai
  });
  if (up.status !== 200 && up.status !== 204) throw new Error(`[${lang}] Upload failed`);

  // Extract title
  const titleMatch = ai.match(/<title[^>]*>([^<]+)<\/title>/i);
  const title = titleMatch?.[1]?.trim() || 'Translated';

  // Confirm
  const conf = await api.frevanaApi.request('POST', '/s3/content/confirm-translate', { new_content_id: prep.newId, title });
  if (!conf.ok) throw new Error(`[${lang}] Confirm failed`);

  let url = conf.data.url || '';
  if (url && !url.startsWith('http')) url = `https://${url}`;

  console.log(`[${lang}] Done`);
  return JSON.stringify({ lang, status: 'new', id: prep.newId, title, url });
};

workflows.script:
module.exports = async function() {
  const results = data['translate-loop'] || '';
  if (!results) return JSON.stringify({ success: false });

  let items = [];
  try {
    const arr = results.trim().startsWith('[') ? JSON.parse(results) : results.split('\n').filter(l => l.trim());
    items = arr.map(item => typeof item === 'string' ? JSON.parse(item) : item);
  } catch (e) {
    return JSON.stringify({ success: false, error: e.message });
  }

  const newCount = items.filter(i => i.status === 'new').length;
  const existCount = items.filter(i => i.status === 'existing').length;

  console.log(`Summary: ${newCount} new, ${existCount} existing`);

  // Format simple markdown
  let md = '# Translation Report\n\n';
  md += `**Total:** ${items.length} | **New:** ${newCount} | **Existing:** ${existCount}\n\n`;
  md += '| Language | Status | URL |\n';
  md += '|----------|--------|-----|\n';
  items.forEach(i => {
    const urlDisplay = i.url ? `[Link](${i.url})` : 'N/A';
    md += `| ${i.lang} | ${i.status} | ${urlDisplay} |\n`;
  });

  return md;
};
